import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../Sidebar/sidebar';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './about.css'; // Import any custom styles

function About() {
  const navigate = useNavigate(); // Use useNavigate here

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      window.location.href = "/";
    }
  }, [navigate]);

  return (
    <>
      <Sidebar />
      <div className="container-fluid d-flex justify-content-center align-items-center vh-100">
        <div className="text-center">
          <h2 className="mb-4">Welcome to the E-Commerce Platform!</h2>
          <p className="lead">
            This application is developed as a project to test and enhance the developer's skills in building full-stack applications. 
            It demonstrates functionalities like user authentication, order management, and more.
          </p>
        </div>
      </div>
    </>
  );
}

export default About;
