"use client";
// import Image from "next/image";
import "./login.css"; // Make sure this file contains your styles
import { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import React from "react";
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify'; // Import Toast components
import 'react-toastify/dist/ReactToastify.css'; // Import toast styles

export default function Login() {
  const Base_Url = 'http://localhost:8000/';
  const navigate = useNavigate(); // Use useNavigate here

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async () => {
    // Check if inputs are empty
    if (!email || !password) {
      toast.error("Please fill in both fields"); // Show toast notification
      return; // Prevent further execution
    }

    try {
      const response = await axios.post(`${Base_Url}users/login`, {
        email,
        password,
      });

      if (response.data.token) {
        localStorage.setItem("authToken", response.data.token);
        // Redirect to Dashboard
        navigate('/dashboard'); // Redirect to the dashboard page
      } else {
        setError("Invalid email or password");
      }
    } catch (error) {
      toast.error("Login failed. Please try again.");
    }
  };

  return (
    <div className="container1">
      <ToastContainer /> {/* Add ToastContainer here */}
      <div className="loginItem">
        <h1 className="text-center fontLogin">
          Login to Your Account
        </h1>

        <h5 className="text-center text-secondary mt-4 fontCommon">
          Welcome to the E-Commerce.
        </h5>
      </div>
      <div className="textBox">
  <div className="inputBoxLeft">
    <div>
      <input
        type="text"
        aria-label="Email"
        className="formcontrol px-3 py-3 textBorder"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
    </div>
    <div>
      <input
        type="password"
        aria-label="Password"
        className="form-control px-3 py-3 mt-3 textBorder"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
    </div>
    {error && <div className="text-danger mt-3">{error}</div>}

    {/* Remember Me and Forgot Password section */}
    <div className="d-flex justify-content-between mt-3">
      <div className="form-check">
        <input
          type="checkbox"
          className="form-check-input"
          id="rememberMe"
        />
        <label className="form-check-label" htmlFor="rememberMe">
          Remember Me
        </label>
      </div>
      <a href="#" className="text-secondary">Forgot Password?</a>
    </div>

    {/* Centered Button */}
    <div className="d-flex justify-content-center mt-3">
      <button
        className="btn px-3 py-3 text-dark loginButton"
        onClick={handleLogin}
      >
        Login to your Account
      </button>
    </div>
  </div>
</div>
    
    </div>
  );
}
