import { useEffect, useState } from 'react';
import "./order.css";
import Sidebar from '../Sidebar/sidebar';
import Popup from '../Popup/popup';
import { useNavigate } from 'react-router-dom';
import Pagination from '../Pagination/paagination';
import 'react-toastify/dist/ReactToastify.css';
import { toast } from 'react-toastify';
import axios from 'axios';





interface OrderItem {
  id: number;
  productName: string;
  quantity: string;
  price: number;
}



function Order() {
  const Base_Url = 'http://localhost:8000/';

  const navigate = useNavigate(); // Use useNavigate here

  const [tableData, setTableData] = useState<OrderItem[]>([]);
  const [formData, setFormData] = useState({ productName: '', quantity: '', price: 0 });
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [popupTitle, setPopupTitle] = useState('');
  const [selectedItem, setSelectedItem] = useState<OrderItem | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(2); 
  const [productName, setproductName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [price, setPrice] = useState("");


  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      window.location.href = "/";
    }
  }, [navigate]);

  useEffect(() => {
    const fetchData = async () => {
      // Simulating an API call with a delay
      const response = await new Promise<OrderItem[]>((resolve) => {
        setTimeout(() => {
          resolve([
           
          ]);
        }, 1000); // Simulate a 1 second delay
      });

      setTableData(response); // Set the fetched data to state
    };

    fetchData();
  }, []);



  const handleAddClick = () => {
    setPopupTitle('Add Order');
    setFormData({ productName: '', quantity: '', price: 0 });
    setSelectedItem(null);
    setIsPopupOpen(true);
  };

  const handleEditClick = (item: OrderItem) => {
    setPopupTitle('Edit Order');
    setFormData({ productName: item.productName, quantity: item.quantity, price: item.price });
    setSelectedItem(item);
    setIsPopupOpen(true);
  };

  const handleDeleteClick = (item: OrderItem) => {
    setTableData((prevTableData) => prevTableData.filter((data) => data.id !== item.id));

  };

  const handleClosePopup =  () => {
    setIsPopupOpen(false);
  };

  // const handleSaveClick = async () => {
  //   if (!productName || !quantity || price) {
  //     toast.error("Please fill in both fields");
  //     return;
  //   }
  //   try {
  //     const response = await axios.post(`${Base_Url}orders`, {
  //       productName,
  //       quantity,
  //       price
  //     });
  //         if (response.data.token) {
  //       localStorage.setItem("authToken", response.data.token);
  //       navigate('/dashboard');
  //     } else {
  //       setError("Invalid email or password");
  //     }
  //   } catch (error) {
  //     toast.error("Login failed. Please try again.");
  //   }
  // };

  const handleSaveClick = async () => {
    // Ensure that all fields are filled
    if (!formData.productName || !formData.quantity || !formData.price) {
      toast.error("Please fill in all fields");
      return;
    }
  
    try {
      const response = await axios.post(`${Base_Url}orders`, {
        productName: formData.productName,
        quantity: formData.quantity,
        price: formData.price
      });
  
      // Assuming your backend returns the newly created order data
      if (response.data) {
        // Optionally, you can add the new order to the state to update the UI
        setTableData((prev) => [...prev, response.data]); // Assuming response.data is the saved order
        
        toast.success("Order saved successfully!");
        setIsPopupOpen(false); // Close the popup after saving
      } else {
        toast.error("Failed to save order");
      }
    } catch (error) {
      toast.error("An error occurred while saving the order");
      console.error(error);
    }
  };
  
  


  

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = tableData.slice(indexOfFirstItem, indexOfLastItem);
  
  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);


  return (
    <>
      <Sidebar />
      <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
        <div className="ps-4 borderTable">
          <div className='d-flex justify-content-between align-items-center'>
            <i className="bi bi-arrow-left backicon" onClick={() => navigate('/dashboard')}></i>
            <span className='order'>Order</span>
            <button className="btn btn-primary ms-auto me-4">Save</button>
          </div>
          <div className='heading d-flex justify-content-between align-items-center'>
            Customer Name
            <input type="text" className='ms-auto me-4 mt-2' />
          </div>
          <div className='heading d-flex justify-content-between align-items-center '>
            Total Quantity
            <input type="text" className='ms-auto me-4 mt-2' readOnly />

          </div>
          <div className='heading d-flex justify-content-between align-items-center'>
            Total Price
            <input type="text" className='ms-auto me-4 mt-2 borderradius-2' readOnly />

          </div>

          <div className="d-flex justify-content-end mt-2">
            <button className="btn btn-primary ms-auto me-4" onClick={handleAddClick}>Add</button>
          </div>
          <table className="table table-bordered mt-2 ">
            <thead>
              <tr>
                <th scope="col">Id</th>
                <th scope="col">Product Name</th>
                <th scope="col">Quantity</th>
                <th scope="col">Price</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {currentItems.map((item) => (
                <tr key={item.id}>
                  <th scope="row">{item.id}</th>
                  <td>{item.productName}</td>
                  <td>{item.quantity}</td>
                  <td>{item.price}</td>
                  <td>
                    <button className="btn " onClick={() => handleEditClick(item)}>
                      <i className="bi bi-pencil-square"></i>
                    </button>
                    <button className="btn " onClick={() => handleDeleteClick(item)}>
                      <i className="bi bi-trash"></i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <Pagination 
            itemsPerPage={itemsPerPage} 
            totalItems={tableData.length} 
            paginate={paginate} 
            currentPage={currentPage} 
          />
        </div>
      </div>

      <Popup isOpen={isPopupOpen} onClose={handleClosePopup} title={popupTitle}>
        <div className="mb-3">
          <input
            type="text"
            name="productName"
            placeholder="Product Name"
            className="form-control mb-2"
            value={formData.productName}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="quantity"
            placeholder="Quantity"
            className="form-control mb-2"
            value={formData.quantity}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="price"
            placeholder="Price"
            className="form-control mb-2"
            value={formData.price}
            onChange={handleInputChange}
          />
        </div>
        <button className="btn btn-primary" onClick={handleSaveClick}>Save Order</button>
      </Popup>
    </>
  );
}

export default Order;
function setError(arg0: string) {
  throw new Error('Function not implemented.');
}
