"use client";
import "./login.css"; // Make sure this file contains your styles
import { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import React from "react";
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const Base_Url = 'http://localhost:8000/';
  const navigate = useNavigate(); // Use useNavigate here

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isCreatingAccount, setIsCreatingAccount] = useState(false); // New state for form toggle
  const [confirmPassword, setConfirmPassword] = useState(""); // New state for confirm password

  const handleLogin = async () => {
    try {
      const response = await axios.post(`${Base_Url}users/login`, {
        email,
        password,
      });

      if (response.data.token) {
        localStorage.setItem("authToken", response.data.token);
        navigate('/dashboard'); // Redirect to the dashboard page
      } else {
        setError("Invalid email or password");
      }
    } catch (error) {
      setError("Login failed. Please try again.");
    }
  };

  const handleCreateAccount = async () => {
    if (password !== confirmPassword) {
      setError("Passwords do not match.");
      return;
    }

    try {
      const response = await axios.post(`${Base_Url}users`, {
        email,
        password,
      });

      if (response.data) {
        setError("Account created successfully! Please log in.");
        setIsCreatingAccount(false); // Toggle back to login after successful account creation
      }
    } catch (error) {
      setError("Account creation failed. Please try again.");
    }
  };

  return (
    <div className="container1">
      <div className="loginItem">
        <h1 className="text-center fontLogin">
          {isCreatingAccount ? "Create Your Account" : "Login to Your Account"}
        </h1>
        <h5 className="text-center text-secondary mt-4 fontCommon">
          Welcome to the E-Commerce.
        </h5>
      </div>
      <div className="textBox">
        <div className="inputBoxLeft">
          <div>
            <input
              type="text"
              aria-label="Email"
              className="form-control px-3 py-3 textBorder"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <input
              type="password"
              aria-label="Password"
              className="form-control px-3 py-3 mt-3 textBorder"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          {isCreatingAccount && (
            <div>
              <input
                type="password"
                aria-label="Confirm Password"
                className="form-control px-3 py-3 mt-3 textBorder"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </div>
          )}
          {error && <div className="text-danger mt-3">{error}</div>}
          <div>
            <button
              className="btn btn-outline-secondary px-3 py-3 mt-3 text-dark loginButton"
              onClick={isCreatingAccount ? handleCreateAccount : handleLogin}
            >
              <span className="d-flex-column">
                <span className="loginSet">
                  {isCreatingAccount ? "Create Account" : "Login to your Account"}
                </span>
              </span>
            </button>
          </div>
          <div className="text-center mt-3">
            <button
              className="btn btn-link text-secondary"
              onClick={() => setIsCreatingAccount(!isCreatingAccount)}
            >
              {isCreatingAccount ? "Already have an account? Login" : "Create New Account"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
