"use client";
import "./login.css"; // Make sure this file contains your styles
import { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import React from "react";
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify'; // Import toast
import 'react-toastify/dist/ReactToastify.css'; // Import toast CSS

export default function Login() {
  const Base_Url = 'http://localhost:8000/';
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isCreatingAccount, setIsCreatingAccount] = useState(false);

  const handleLogin = async () => {
    try {
      const response = await axios.post(`${Base_Url}users/login`, {
        email,
        password,
      });

      if (response.data.token) {
        localStorage.setItem("authToken", response.data.token);
        navigate('/dashboard');
      } else {
        setError("Invalid email or password");
      }
    } catch (error) {
      setError("Login failed. Please try again.");
    }
  };

  const handleCreateAccount = async () => {
    try {
      const response = await axios.post(`${Base_Url}users`, {
        username,
        name,
        email,
        password,
      });

      if (response.data) {
        toast.success("Account created successfully! Please log in."); // Show success toast
        setIsCreatingAccount(false); // Toggle back to login
        clearFields(); // Clear fields after successful account creation
      }
    } catch (error) {
      setError("Account creation failed. Please try again.");
    }
  };

  const clearFields = () => {
    setUsername("");
    setName("");
    setEmail("");
    setPassword("");
    setError("");
  };

  return (
    <div className="container1">
      <ToastContainer /> {/* Toast container for displaying messages */}
      <div className="loginItem">
        <h1 className="text-center fontLogin">
          {isCreatingAccount ? "Create Your Account" : "Login to Your Account"}
        </h1>
        <h5 className="text-center text-secondary mt-4 fontCommon">
          Welcome to the E-Commerce.
        </h5>
      </div>
      <div className="textBox">
        <div className="inputBoxLeft">
          {isCreatingAccount && (
            <>
              <div>
                <input
                  type="text"
                  aria-label="Username"
                  className="form-control px-3 py-3 textBorder"
                  placeholder="Username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div>
                <input
                  type="text"
                  aria-label="Name"
                  className="form-control px-3 py-3 mt-3 textBorder"
                  placeholder="Full Name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
            </>
          )}
          <div>
            <input
              type="text"
              aria-label="Email"
              className="form-control px-3 py-3 mt-3 textBorder"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <input
              type="password"
              aria-label="Password"
              className="form-control px-3 py-3 mt-3 textBorder"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          {error && <div className="text-danger mt-3">{error}</div>}
          <div>
            <button
              className="btn  px-3 py-3 mt-3  loginButton1"
              onClick={isCreatingAccount ? handleCreateAccount : handleLogin}
            >
              <span className="d-flex-column">
                <span className="loginButton1">
                  {isCreatingAccount ? "Create Account" : "Login to your Account"}
                </span>
              </span>
            </button>
          </div>
          <div className="text-center mt-3">
            <button
              className="btn btn-link text-secondary"
              onClick={() => {
                setIsCreatingAccount(!isCreatingAccount);
                clearFields(); // Clear fields when toggling
              }}
            >
              {isCreatingAccount ? "Already have an account? Login" : "Create New Account"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
